    transition: background-color 0.2s;

    display: flex;
    align-items: center;
    justify-content: center;

    svg {
      width: 20px;
      height: 20px;
      color: #312e28;
    }

    &:hover {
      background: ${shade(0.2, '#ff9000')};
    }
  }
`;
